import styled , {keyframes} from "styled-components";

export const CarouselHover = styled.div`
  display: flex;
  -webkit-filter: brightness(100%);


  &:hover {
    -webkit-filter: brightness(12%);
    -webkit-transition: all 1s ease;
    -moz-transition: all 1s ease;
    -o-transition: all 1s ease;
    -ms-transition: all 1s ease;
    transition: all 1s ease;
  }

  &:not(:hover) {
    -webkit-filter: brightness(100%);
    -webkit-transition: all 1s ease;
    -moz-transition: all 1s ease;
    -o-transition: all 1s ease;
    -ms-transition: all 1s ease;
    transition: all 1s ease;
  }
`;

const scrollAnimation = keyframes`
  0% {
    transform: translateY(0);
  }
  100% {
    transform: translateY(-100%);
  }
`;

export const ScrollableTextContainer = styled.div`
  overflow: hidden;
  height: 100%; /* Certifique-se de que a altura seja suficiente para mostrar todo o texto */
  animation: ${scrollAnimation} 10s linear infinite; /* Ajuste a duração conforme necessário */
`;
